import React, { Component } from 'react';
import '../caixaDeTexto/style.css'

class CaixaDeTexto extends Component {


    constructor(props){
        super(props);
        this.state = {
            placeholder: props.placeholder
        }
    }

    render() {
        return (
            <input placeholder={this.state.placeholder}>
            
            </input>
        )
    }


}

export default CaixaDeTexto;