import React, { Component } from 'react';
import '../senha/style.css'

class Senha extends Component {

constructor(props) {
    super(props);
    this.state = {
        msg: props.msg
    }
}

    render(){
        return(
            <input type="password" placeholder={this.state.msg}></input>
        )
    }
}

export default Senha;